!function(){"use strict";let isWaiting=!0;const html=document.getElementsByTagName("html")[0];async function fetchTimeZone(){const worldTimeURL="https://worldtimeapi.org/api/ip";await fetch(worldTimeURL).then(response=>response.json()).then(data=>{const userCurrentData=data,abbr=document.getElementById("abbr"),weekNumber=document.getElementById("weekNumber"),dayOfWeek=document.getElementById("dayOfWeek"),dayOfYear=document.getElementById("dayOfYear");abbr.innerHTML=userCurrentData.abbreviation,weekNumber.innerHTML=userCurrentData.week_number,dayOfWeek.innerHTML=userCurrentData.day_of_week,dayOfYear.innerHTML=userCurrentData.day_of_year,timezone.innerHTML=userCurrentData.timezone}).then(()=>{isWaiting=!1,html.classList.add("not-loading"),html.addEventListener("transitionend",()=>{html.classList.remove("is-loading")})})}async function fetchCountryCity(){const freeGeoURL="https://freegeoip.app/json/";await fetch(freeGeoURL).then(response=>response.json()).then(data=>{const locationData=data,city=document.getElementById("city"),country=document.getElementById("country");city.innerHTML=locationData.city,country.innerHTML=locationData.country_code})}isWaiting&&html.classList.add("is-loading"),fetchTimeZone(),fetchCountryCity()}();